<?phpif( !class_exists( 'mythemes_plg_cfg' ) ){class mythemes_plg_cfg{    /* RESOURCES */    private static $posts       = array();    private static $boxes       = array();    private static $taxonomies  = array();        /* OPTIONS */    private static $pages       = array();    private static $deff        = array(        'settings' => array(),        'meta' => array()    );    /* GET AND SET CUSTOM POSTS */    public static function get_posts(){        return self::$posts;     }    public static function set_posts( $posts ){        self::$posts = $posts;    }    /* GET AND SET META POXES FOR STANDARD AND CUSTOM POSTS */    public static function get_boxes(){        return self::$boxes;     }    public static function set_boxes( $boxes ){        self::$boxes = $boxes;    }    /* GET AND SET TAXONOMIES FOR CUSTOM POSTS */    public static function get_taxonomies(){        return self::$taxonomies;     }    public static function set_taxonomies( $taxonomies ){        self::$taxonomies = $taxonomies;    }    /* GET AND SET PAGES & OPTIONS */    public static function get_pages(){        return self::$pages;     }    public static function set_pages( $pages ){        self::$pages = $pages;    }    /* GET AND SET DEFAULT & VALUES FOR META AND OPTIONS */    public static function get_deff(){        return self::$deff;     }    public static function set_deff( $deff ){        self::$deff = $deff;    }}} /* END IF CLASS EXISTS */?>